INSERT INTO `estate` (`id`, `name`, `type`, `market`, `rooms`, `walls`, `totalSq`, `residentialSq`, `kitchenSq`, `floor`, `superficiality`, `heating`, `yearOfConstruction`, `utilitiesWinter`, `utilitiesSummer`, `registrationNumber`, `price`, `country`, `oblast`, `city`, `adress`, `coord_x`, `coord_y`, `description`, `arenda_prodazha`, `remont`, `mebel`, `posle_stroiteley`, `parking`, `terasa`, `penthouse`) VALUES
(8, 'ЖК Гостомельські Липки 5', 1, 2, 4, 1, 100, 80, 20, 8, 10, 2, 6, 5000, 2000, '321143', 21000, 'Украина', 'Киевская', 'Гостомель', ' вул. Молодіжна, 11/4', 50.5791, 30.2151, '<p>\r\n	ЖК &laquo;Гостомельські Липки 5&raquo; будують в смт. Гостомель Бучацького району Київської області за адресою вул. Молодіжна, 11/4, 11/5. Новобуд знаходиться на відстані 8 км від меж Києва. До станції метро &laquo;Житомирська&raquo; на машині можна дістатися приблизно за 25 хвилин. Житловий комплекс комфорт-класу складається з чотирьох 4-поверхових будинків. Компанія &laquo;Друзі Девелопмент&raquo; запланувала завершити роботу в II кварталі 2022 року. Відстежувати хід будівництва можна на офіційному сайті девелопера, де публікують свіжі фото з будівельного майданчика.</p>\r\n', 1, 1, 0, 1, 0, 0, 0),
(9, 'ЖК SILENT PARK (Сайлент Парк)', 1, 2, 4, 4, 140, 100, 30, 6, 10, 2, 6, 3000, 1500, '545345', 35000, 'Украина', 'Львовская', 'Львов', 'ул. Орлика', 49.874, 24.0332, '<h2 id=\"silentpark\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Коротка інформація про ЖК SILENT PARK</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс зводиться забудовником LEV Development. Об&#39;єкт складається з двох сучасних будинків, а здача першої секції запланована на 4 квартал 2024 року. Знайти новобудову можна в Шевченківському районі Львова на вулиці П. Орлика. Сайлент Парк &mdash; це житло бізнес-класу, яке включає все, що потрібно сучасній людині та навіть трохи більше.</p>\r\n<h2 id=\"\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Ключові особливості та відмінності</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	У будівництві використовується монолітно-каркасна технологія, для стін було обрано якісний керамоблок. Зовнішнім утеплювачем є мінеральна вата. Таке поєднання будівельних матеріалів забезпечує не лише енергоефективність, а ще й пожежну безпеку. У кожній квартирі встановлено індивідуальне опалення. Будинок розрахований на 6 поверхів і включатиме 69 квартир з різною кількістю кімнат. Перший поверх буде відданий під комерційне використання. На шостому поверсі розміщено житло представницького класу із збільшеною висотою стель. У той час як в інших квартирах ЖК їхня висота становить 2,7 метри, на шостому поверсі вони досягають 3 метрів. Для входу на поверх кожному власнику видається спеціальний електронний ключ, який забезпечує додатковий захист від небажаних відвідувачів.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Фасад SILENT PARK виконаний у стилі європейського мінімалізму зі збереженням вільного простору та відчуття &laquo;повітря&raquo;. Розробкою дизайну холів займалися спеціалісти студії SAVYTSKYYdesign, які чудово передали філософію комплексу. Планування будівлі включає закрите підземне паркування, куди можна спуститися прямо на ліфті, тому вам не доведеться взимку зчищати з машини сніг і витрачати час на прогрівання двигуна. Територія навколо будинку також закрита від сторонніх, а отже &mdash; жодних сумнівних громадян біля вашого під&#39;їзду, чистота, спокій за свою дитину, яка грає з іншими дітьми на майданчику, а також відсутність зайвого галасу.</p>\r\n<h2 id=\"silentpark-1\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Інфраструктура ЖК SILENT PARK і поруч</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	На території передбачені зони для відпочинку, ігрові майданчики для дітей і спортивні майданчики для любителів активного способу життя. Поряд з будинком ви знайдете безліч зелені та незайманих ділянок лісу, де можна погуляти з собакою або вирушити на вечірню пробіжку. Від ЖК неважко дістатися до навчальних і дошкільних закладів, поряд є медичні установи, магазини, торговий центр, ринок і банк. Любителів піших прогулянок порадує близькість Замарстинівського парку, де можна провести час з сім&#39;єю. У пішій доступності розташовані зупинки громадського транспорту, тому ви легко дістанетеся до центру навіть без особистого авто. Детальніше ознайомитися з розташуванням SILENT PARK вам допоможе онлайн-карта нашого сайту з позначкою адреси новобудови.</p>\r\n<h2 id=\"-1\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Планування та продаж квартир</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Налаштовані купити квартиру в ЖК SILENT PARK? Тоді варто звернутися до представників забудовника у відділ продажу. Досвідчені консультанти ознайомлять вас з варіантами доступного для покупки житла з однією, двома або трьома кімнатами, розкажуть все про ціни, умови розстрочки або кредиту, а також покажуть більше фото та дизайнів проекту обраного вами варіанту.</p>\r\n', 1, 1, 1, 0, 0, 0, 0),
(10, 'ЖК Волошковий', 1, 2, 4, 1, 140, 100, 20, 7, 10, 1, 5, 3000, 2000, '3534534', 34000, 'Украина', 'Винницкая', 'Винница', 'вул. Волошкова(Андрія Іванова), 4в', 49.2248, 28.5224, '<h2 id=\"\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Про житловий комплекс</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс Волошковий споруджується забудовником ОК ЖБК Волошковий. ЖК належить до новобудов комфорт класу. Здача об&#39;єкту в експлуатацію запланована на 4 квартал 2024 року. В процесі будівництва знаходиться 1 будинок на 2 під&#39;їзда.</p>\r\n<h2 id=\"-1\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Розташування</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	ЖК Волошковий розташований в Вінниці. Новобудову можна знайти безпосередньо в районі Замостянський по вул. Волошкова(Андрія Іванова), 4в. Також варто звернути увагу на карту вище. Це допоможе зорієнтуватися без проблем, де саме будується ЖК і як до нього доїхати, якщо ви вирішили особисто перевірити хід будівництва. Карта також дозволить оцінити зручність локації для майбутнього проживання.</p>\r\n<h2 id=\"-2\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Технічні дані</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Новобуд зводять за технологією &ndash; цегла, монолітно - каркасна. Кількість поверхів у будинку &ndash; 9, висота стель &ndash; 2.7 м. Опалення &ndash; індивідуальне. Докладно розглянути доступні планування житла в цьому будинку, оцінити їх зручність та обрати конкретний варіант можна на нашому сайті або у відділі продажу. Якщо вас цікавить, де після покупки житла вам доведеться паркувати автомобіль, варто звернути увагу на наявність паркінгу чи стоянки біля будинку. В цьому житловому комплексі є паркування.</p>\r\n<h2 id=\"-3\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Особливості продажу квартир</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс Волошковий на DOM.RIA представлений 41 квартирою. В новобудові можна купити нерухомість за такими цінами від забудовника:</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	*1-кімнатні &ndash; площа від 36.83 і до 46.87 квадратних метрів, вартість від 1 031 240 грн і до 1 218 620 грн;</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	*2-кімнатні &ndash; площа від 68.56 і до 70.47 квадратних метрів, 1 782 560 грн і до 1 832 220 грн;</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Про можливість придбання житла в кредит або в розстрочку, можна дізнатися на цій же сторінці або зв&#39;язавшись з представником забудовника.</p>\r\n<h2 id=\"-4\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Інфраструктура</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс Волошковий відрізняється зручним розташуванням, оскільки в безпосередній близькості від нього є: школа, дитячий садок, тц, ринок, магазини, транспортна розв&#39;язка, парковка гостьового типу відкрита без охорони. Інфраструктуру навколо новобудови та відстань від будинку до необхідних об&#39;єктів можна оцінити на карті.</p>\r\n', 1, 1, 0, 1, 1, 0, 0),
(11, 'ЖК Hvoya (Хвоя)', 1, 2, 4, 1, 180, 160, 20, 6, 10, 2, 6, 6000, 3000, '53432', 59700, 'Украина', 'Киевская', 'Киев', 'вул. Миколи Юнкерова, 37', 0, 0, '<h2 id=\"\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Про житловий комплекс</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	ЖК Hvoya будують в Оболонському районі Києва за адресою вул. Миколи Юнкерова, 37. Новобудова розташована за 18 хвилин їзди на машині до станції метро &laquo;Академмістечко&raquo;. Зовсім поруч знаходиться історична територія Пуща-Водиця.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс комфорт-класу являє собою чотириповерховий будинок з ліфтами, дизайнерськими під&#39;їздами та ландшафтним озелененням, будинок оснащений алюмінієвими вікнами і броньованими дверима з мдвф накладками. Будівництво проводить компанія ZIM Capital Group. На її офіційному сайті можна переглянути візуалізації проекту. Відстежувати хід будівельних робіт зручно по фото на цій сторінці.</p>\r\n<h2 id=\"-1\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Особливості</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Hvoya будується за монолітно-каркасною технологією. Стіни заповнюють керамоблоком. У новобудові пропонують купити квартиру з обробкою під чистову, що прискорює заселення та зменшує час на проведення ремонту.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Переваги житлового комплексу &laquo;Хвоя&raquo;:</p>\r\n<ul style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	<li>\r\n		18 хвилин їзди до метро;</li>\r\n	<li>\r\n		поряд парк &laquo;Пуща-Водиця&raquo;;</li>\r\n	<li>\r\n		закрита територія;</li>\r\n	<li>\r\n		автостоянка, підземний паркінг;</li>\r\n	<li>\r\n		зона для занять спортом;</li>\r\n	<li>\r\n		дитячий майданчик;</li>\r\n	<li>\r\n		малоповерхова забудова;</li>\r\n	<li>\r\n		альтернативна лінія електромережі;</li>\r\n	<li>\r\n		комерційні приміщення для бізнесу;</li>\r\n	<li>\r\n		індивідуальне опалення;</li>\r\n	<li>\r\n		висота стелі &mdash; 2,75 метри.</li>\r\n</ul>\r\n<h2 id=\"-2\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Інфраструктура</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	На закритій території Hvoya розташовані ігрова зона для дітей та місця для відпочинку.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	По карті можна побачити, що поблизу ЖК розташовані Міжнародна французька школа, школа №104, відділення банку, трамвайна та автобусна зупинки, &laquo;Нова пошта&raquo;. Неподалік знаходяться дитячий садок №52, військова лікарня, Міжнародна тенісна академія. Поруч безліч місць для відпочинку на природі, зокрема муніципальний пляж &laquo;Пуща-Водиця&raquo; та паркова зона. На відстані 2,5 км від будинку розташований Міжнародний дитячий центр &laquo;Артек&raquo;.</p>\r\n<h2 id=\"-3\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Продаж квартир і планування</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	У забудовника продаються різні варіанти житла, причому покупець може замовити зміну планування з урахуванням особистих потреб і бажань. Щоб купити квартиру в ЖК Hvoya, потрібно зробити повну оплату при укладанні угоди. Чи можна оформити розстрочку чи кредит, варто уточнити окремо.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Дізнатися ціни на квадратні метри або записатися на перегляд новобудови для підбору квартир можна при відвідуванні офісу компанії. Приходити до відділу продажу особисто необов&#39;язково, адже можна отримати необхідну інформацію і по телефону: номер розміщено на цій сторінці.</p>\r\n', 1, 1, 0, 0, 1, 1, 0),
(12, 'ЖК Creator City (Креатор Сіті)', 1, 2, 4, 1, 400, 300, 50, 4, 10, 4, 6, 4000, 1500, '5345345', 61000, 'Украина', 'Киевская', 'Киев', 'вул. Дегтярівська, 17-19', 0, 0, '<h2 id=\"\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Про житловий комплекс Креатор Сіті</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс Creator City зводять у Шевченківському районі Києва за адресою: вулиця Дегтярівська, 17-19. Будівництвом масиву займається компанія Креатор-Буд, який працює на ринку нерухомості понад 16 років.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Creator City &mdash; це багатофункціональний житловий комплекс бізнес-класу з 8-ми будинків в Шевченківському районі. Будинки об&#39;єднані спільною прибудинковою територією &mdash; житловим простором для відпочинку, прогулянок і спорту, з ландшафтним парком площею 2 га та власною водоймою, дитячими ігровими зонами, прогулянковими алеями, спортивними майданчиками та зонами вигулу домашніх улюбленців.&nbsp;</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Заявлена здача першого будинку &ndash; 4 квартал 2021 року, другого &ndash; 2 квартал 2023 року та третього будинку &ndash; 1 квартал 2024 року. Станом на 13 червня 2022 року у першому будинку ведуться роботи з благоустрою території, що говорить про велику ймовірність своєчасної здачі новобудови.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	На сайті DOM.RIA доступна інформація про хід будівництва комплексу, фото об&#39;єкта, його розташування на карті, а також відгуки тих, хто взаємодіяв із забудовником та готовий поділитися своїм досвідом консультації, візиту до відділу продажу або етапами придбання житла.</p>\r\n<h2 id=\"creatorcity\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Особливості Creator City</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	ЖК Creator City спроектований за принципом &laquo;місто в місті&raquo; &mdash; все необхідне для життя знаходиться на території комплексу. Як і в будь-якому сучасному місті, будинки відрізняються один від іншого поверховістю і архітектурним різноманіттям &mdash; у кожного будинку свій характер і стиль, об&#39;єднані загальною концепцією. На перших поверхах розташуються спортивний комплекс з басейном, супермаркет, дитячий садок, спа і салон краси, а також медичний центр.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Для автомобілів заплановано підземний паркінг з ліфтом. На звільненому від паркувальних місць просторі будуть сформовані озеленені зони. Власна прибудинкова територія, доброзичливе міське середовище, дитяча безпека, сприятлива екологія (немає шуму і вихлопних газів), унікальний дизайн середовища &mdash; це те, що сприятиме комфортному відпочинку.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	В кожній внутрішньодворовій території передбачені майданчики для сімейного відпочинку із закритим доступом, призначені лише для мешканців та їх гостей. Із закритих зон в можна потрапити до ландшафтного парку, що веде вглибину території житлового комплексу.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Дизайнерські лобі житлового комплексу Creator City матимуть лаунж-зону, колясочну, багатофунціональну вбиральню із зоною для догляду за дитиною та місцем для миття лапок домашніх улюбленців. У всіх секціях заплановане панорамне скління, що гарантує доступ природного освітлення в кімнати.</p>\r\n<h2 id=\"-1\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Інфраструктура</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс знаходиться у добре розвиненому районі міста. За 6 хвилин пішки можна дійти до дитячих садків та навчальних закладів. Практично за такий самий час &ndash; 6-10 хвилин &ndash; можна дійти до лікарні та медичних центрів. Поруч розташована велика кількість магазинів, супермаркетів, салонів та кав&#39;ярень. За 2 хвилини можна дістатися паркових зон.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Окрему увагу забудовник приділяє внутрішній інфраструктурі. На території комплексу будують дитячий садок, спортивний комплекс та приватну клініку. Відповідно до проєкту у комплексі передбачено супермаркет, салони краси та СПА. Крім того, 2 га землі відведено спеціально для облаштування рекреаційних зон: власного кінотеатру на відкритому повітрі, декоративного ставка та парку. Вишуканою особливістю комплексу стане сад із сакурами та японський ресторан.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс ідеально підійде для майбутніх учнів та студентів. Поруч 4 провідних ВУЗи: КПІ ім. Ігоря Сікорського, Київський національний економічний університет, Інститут міжнародних відносин, Національний медичний університет ім. О.О. Богомольця.</p>\r\n<h2 id=\"-2\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Продаж квартир та планування</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	У житловому комплексі &laquo;Creator City&raquo; у продажу доступні 1-2-3-5 та більше кімнатні квартири, багаторівневе житло, комерційні приміщення, комори та місця у паркінгу. Однокімнатних квартир у продажу 70, майбутній інвестор може вибрати житло серед 19 варіантів планувань площею від 31 м&sup2;. Двокімнатних квартир у продажу 36 у 9 варіантах конфігурацій. Мінімальна площа житла із двома кімнатами стартує від 52 м&sup2;. Трикімнатні квартири у продажу 13, які можна вибрати серед 5 варіантів планувань площею від 73 м2. П&#39;ятикімнатні квартири представлені в одному варіанті у продажу в єдиному вигляді конфігурацій площею 128 м&sup2;.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Інвестор також може придбати багаторівневі квартири, яких у продажу є 5 з різним видом планування на кожну квартиру. Площа багаторівневих квартир варіюється від 70 м&sup2; до 204 м&sup2;. Комори доступні в 11 варіантах планувань площею від 1,8 м&sup2; до 13 м&sup2;. Комерційні приміщення доступні у 10 варіантах конфігурацій, які площа варіюється від 43 м&sup2; до 278 м&sup2;. Паркінг можна вибрати серед 18 видів планувань площею від 12 м&sup2; до 19 м&sup2;.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Придбати квартиру чи приміщення у комплексі можна за допомогою розстрочки під 0% на строк до 31 грудня 2022 року. Залежно від номера будинку та секції відповідно термін розстрочки може бути продовжений до 31 березня 2024 року. Оформити розстрочку можна за умови оплати від 30% від повної вартості житла як перший внесок.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Дізнатись актуальну вартість м&sup2; можна у розділі &laquo;Ціни на квартири та приміщення в ЖК &laquo;Creator City&raquo; від забудовника&raquo;, а для отримання детальної інформації можна звернутися до відділу продажу забудовника.</p>\r\n', 0, 1, 0, 0, 1, 0, 1),
(13, 'ЖК Сирецькі Сади', 1, 2, 4, 1, 200, 160, 30, 6, 10, 1, 7, 4000, 2000, '545345', 38000, 'Украина', 'Киевская', 'Киев', 'вул. Маршала Гречка, 10б', 0, 0, '<h2 id=\"\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Про житловий комплекс</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	ЖК &laquo;Сирецькі Сади&raquo; розташований в Подільському районі Києва за адресою вул. Маршала Гречко, 10б. Звідси за 20 хвилин на машині можна доїхати до центру столиці. У 30 хв. ходьби знаходиться станція метро &laquo;Сирець&raquo;.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс комфорт-класу складається з 10 будинків висотою від 12 до 24 поверхів. Будівництво проводить забудовник &laquo;Інтергал-Буд&raquo;. Об&#39;єкт планують добудувати та здати в експлуатацію в III кварталі 2022 року.</p>\r\n<h2 id=\"-1\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Переваги</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Новобудови зводять за монолітно-каркасною технологією. Для зовнішніх стін використовується керамічна червона цегла, фасад утеплюють мінеральною ватою. Опалення &ndash; автономне. Квартири передають власникам з різними типами обробки &ndash; з чорновим або повноцінним ремонтом.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Переваги ЖК на вулиці Маршала Гречко, 10-б:</p>\r\n<ul style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	<li>\r\n		<p>\r\n			концепція &laquo;місто в місті&raquo; та &laquo;двір без авто&raquo;;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			закрита територія з відеоспостереженням;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			відкритий паркінг з охороною;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			алея з великими ялинками, центральна площа з зонами відпочинку, фруктовий сад;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			поблизу дендропарк &laquo;Сирець&raquo; та парк &laquo;Дубки&raquo;;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			панорамне скління на верхніх поверхах;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			можливість купити житло з ремонтом &laquo;під ключ&raquo;;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			комори та колясочні;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			комерційні приміщення для бізнесу;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			свій дитячий сад.</p>\r\n	</li>\r\n</ul>\r\n<h2 id=\"-2\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Прилегла інфраструктура</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Комплекс буде розділений на окремі квартал-сади, в кожному з яких облаштують свою територію відпочинку. У дворах встановлять дитячі майданчики з прогумованим покриттям, зроблять велосипедні доріжки та зони для занять спортом. Поруч з будинком будуть фруктовий сад, водойма з декоративним містком, прогулянкова алея.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Інфраструктура включає в себе супермаркет, магазини, пошту, аптеки, салон краси, хімчистку, кафе, банк. У 5 хвилинах ходьби розташовані школа та медичний центр.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Як видно на карті, в пішій доступності знаходяться два парки. Незабаром поблизу відкриються дві станції метро &ndash; &laquo;Проспект Правди&raquo; та &laquo;Мостицька&raquo;.</p>\r\n<h2 id=\"-3\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Продаж квартир та планування</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Забудовник продає нерухомість з різним плануванням в залежності від потреб покупців. У ЖК можна купити 1-2-3-кімнатні квартири по 100% оплаті або в розстрочку. Фото доступних планувальних рішень можна подивитися на сайті об&#39;єкта. Уточнити ціну житлоплощі, яка сподобалась, можна у відділі продажів за вказаним на цій сторінці номером.</p>\r\n', 0, 1, 1, 1, 1, 0, 0),
(14, 'ЖК Козацький', 1, 2, 6, 1, 180, 140, 30, 4, 13, 1, 6, 4000, 1500, '553', 23000, 'Украина', 'Ивано-франковская', 'Иванофранковск', 'вул. Довженка О.(Веселова), Мазепи', 0, 0, '<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Про житловий комплекс</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	ЖК &laquo;Козацький&raquo; розташований на території мікрорайону Бам в місті Івано-Франківськ за адресою вул. О. Довженка/вул. Виноградна. Це тиха та затишна локація з гарною інфраструктурою та налагодженим транспортним сполученням. Житловий комплекс комфорт-класу складається з п&#39;яти 10-поверхових будинків. Будівництво об&#39;єкта проводить забудовник Socium Developer. Девелопер планує закінчити роботу та здати новобуд в експлуатацію в II кварталі 2022 року. На офіційному сайті будівельної компанії регулярно розміщують фото етапів зведення новобудов.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Особливості та переваги</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	ЖК будують по цегляній технології з заповненням стін керамічною червоною цеглою. Для утеплення фасаду застосовують пінополістирол. У кожному новому будинку здають квартири з чорновим ремонтом та індивідуальним опаленням.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Переваги новобудов на вулицях О. Довженка та Виноградна:</p>\r\n<ul style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	<li>\r\n		<p>\r\n			центральна частина міста;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			закриті безпечні двори;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			своя система охорони та відеоспостереження;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			ігрові та спортивні майданчики;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			сучасний підземний паркінг;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			комерційні приміщення для бізнесу;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			дитячий центр площею більше 400 м&sup2;;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			панорамні вікна;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			кухні з французькими балконами;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			лоджія в кожній квартирі;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			у продажу комори.</p>\r\n	</li>\r\n</ul>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Об&#39;єкти інфраструктури</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Внутрішня інфраструктура комплексу включає в себе гаражні приміщення, спортивні та ігрові майданчики, великий дитячий центр та інші заклади, які відкриються на перших поверхах житлових будинків. Новобуд розташований поруч з льодовою ареною та аквапарком &laquo;Цунамі&raquo;. У пішій доступності знаходяться 2 школи, дитсадок, дитяча лікарня, автостанція, продуктові магазини, авторинок, стадіон. Як показує карта, неподалік є супермаркет, відділення пошти, зупинка громадського транспорту.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Продаж квартир та планування</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Безпосередньо у забудовника можна купити житло з великим вибором планувань в залежності від потреб та фінансових можливостей покупців. У новобудовах продаються 1-2-3-кімнатні квартири, а також комори та машиномісця. Дізнатися вартість гаражного боксу, ціни на нерухомість або умови розстрочки можна у відділі продажів. Зв&#39;язатися з ним вийде за вказаними на сторінці ЖК номерами.</p>\r\n', 0, 1, 1, 1, 1, 1, 0),
(15, 'ЖК West Towers (Вест Тауерс)', 1, 2, 4, 1, 200, 160, 30, 4, 10, 2, 6, 3000, 1000, '434324', 27000, 'Украина', 'Закарпатская', 'Ужгород', 'вул. Легоцького, 64а, 64б', 0, 0, '<h2 id=\"\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Про житловий комплекс</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	ЖК West Towers будують в районі Новий в Ужгороді за адресою вул. Легоцького, 64б. Новобудови розташовані за 10 хвилин їзди до центру міста, позаду будівлі Закарпатського державного університету.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс класу комфорт складається із шести 9-поверхових будинків. Будівництво веде компанія IDM GROUP, яка запланувала завершити роботу в IV кварталі 2023 року. На сайті забудовника можна переглянути фото, як виглядатиме готовий об&#39;єкт і його територія. Відстежувати динаміку будівництва будівлі зручно в соціальних мережах девелопера.</p>\r\n<h2 id=\"-1\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Особливості житлового комплексу</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	West Towers зводять із використанням цегляної технології. Стіни заповнюють керамічною червоною цеглою та утеплюють плитами пінополістиролу. Фасадну частину буде виконано у стриманому європейському стилі.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	У новобудові можна купити нерухомість, як з чорновим оздобленням, так і з ремонтом &laquo;під ключ&raquo;. Останній варіант буде особливо зручний покупцям, які хочуть швидше заселитися у нове житло. У першому випадку наповнення включає вхідні броньовані двері, шестикамерні вікна, чорнову стяжку та штукатурку, встановлення бойлеру, радіаторів, котла, домофону, розведення проводки, підведення кабельного телебачення, інтернету.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Переваги житлового комплексу &laquo;Вест Тауерс&raquo;:</p>\r\n<ul style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	<li>\r\n		закрита територія;</li>\r\n	<li>\r\n		цілодобове відеоспостереження, огорожу та шлагбаум на в&#39;їзді;</li>\r\n	<li>\r\n		Wi-Fi у дворі;</li>\r\n	<li>\r\n		своя паркова зона та місця для відпочинку;</li>\r\n	<li>\r\n		багато дитячих майданчиків;</li>\r\n	<li>\r\n		футбольне поле;</li>\r\n	<li>\r\n		стаціонарна зарядна станція для електромобілів;</li>\r\n	<li>\r\n		концепція &laquo;двір без машин&raquo;;</li>\r\n	<li>\r\n		підземний паркінг, гаражі, окремі паркомісця;</li>\r\n	<li>\r\n		немає комерційних приміщень;</li>\r\n	<li>\r\n		гарні вхідні групи;</li>\r\n	<li>\r\n		безшумні вантажопасажирський та пасажирський ліфти у кожному будинку;</li>\r\n	<li>\r\n		інтернет, домофон і кабельне телебачення у квартирах;</li>\r\n	<li>\r\n		висота стелі &ndash; 2,7 метри;</li>\r\n	<li>\r\n		індивідуальне опалення від електричного казана;</li>\r\n	<li>\r\n		панорамні вікна та балкони;</li>\r\n	<li>\r\n		мальовничий вигляд, який ніколи не загородять інші будинки.</li>\r\n</ul>\r\n<h2 id=\"-2\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Об&#39;єкти інфраструктури</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Квартал West Towers матиме власний парк нового рівня, де можуть відпочивати всі мешканці новобудов. У цьому громадському просторі розмістять місця для відпочинку, сучасні ігрові комплекси для дітей різного віку, футбольне поле, алеї для прогулянок. Двори в ЖК безпечні, тому що вільні від машин.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Забудовник виділяє як перевагу і те, що у нових будинках не буде комерційних приміщень. Будинки збудовані на відстані одна від одної, щоб створювалося відчуття вільного простору, максимально наповненого сонячним світлом.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Від новобудови можна пішки дійти до ТЦ &laquo;Новий&raquo;, ТЦ Tokyo, кількох кафе та магазинів, аптеки, відділення пошти, автобусної зупинки. Поруч є школа, дитячий садок, супермаркет &laquo;АТБ-Маркет&raquo;, ветеринарна клініка, &laquo;Нова пошта&raquo;. За 20 хвилин ходьби знаходиться Боздоський парк.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Як видно на карті, на машині до центру Ужгорода можна доїхати за 10 хвилин. Стільки ж займає дорога до автовокзалу та залізничного вокзалу.</p>\r\n<h2 id=\"-3\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Купівля та планування</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	У забудовника можна придбати безпосередньо однокімнатну або двокімнатну нерухомість з широким вибором планувань залежно від потреб та бюджету клієнта. Планувальні рішення передбачають великі кухні-студії, роздільні санвузли, панорамне скління для гарної інсоляції.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Компанія пропонує купити квартиру в ЖК West Towers за повною передоплатою або оформити розстрочку на зручний термін із першим внеском у розмірі 30-50%.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Щоб дізнатися точні ціни на квадратні метри, умови покупки або поцікавитись наявністю акційних пропозицій, зателефонуйте до відділу продажу. Зв&#39;язатися з ним можна за номером, вказаним на цій сторінці. Ви можете записатися також на перегляд новобудови, щоб особисто оцінити всі переваги проекту та вибрати житлоплощу на місці.</p>\r\n', 1, 1, 1, 0, 0, 0, 0),
(16, 'ЖК Milltown (Міллтаун)', 1, 2, 4, 1, 140, 80, 20, 9, 10, 1, 7, 4000, 2000, '4543535', 77159, 'Украина', 'Львовская', 'Львов', 'вул. Лемківська, 9, просп. Чорновола', 0, 0, '<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс &quot;Mill Town&quot; &mdash; сучасна новобудова бізнес-класу у Львові, яка відрізняється каскадною поверховістю, мінімалістичним зовнішнім оздобленням і продуманими внутрішніми ергономічними плануваннями, що не вимагають подальшого додаткового втручання.</p>\r\n<h2 id=\"milltown\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Ключові особливості житлового комплексу &quot;Milltown&quot;</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Головна особливість ЖК &quot;MillTown&quot; &mdash; піша доступність до торгово-розважального центру &quot;Forum Lviv&quot;, який користується найбільшою популярністю як серед місцевих жителів, так і серед туристів.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Не менш важливо розглянути позитивні технічні характеристики, на які вказує девелопер &quot;Інтергал-Буд&quot;:</p>\r\n<ul style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	<li>\r\n		<p>\r\n			Продуманий поділ житлового комплексу на житлову зону (6 секцій) і комерційний сектор (4 секції);</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Охоронюваний підземний паркінг, який передбачений виключно для мешканців цього ЖК і розрахований на достатню кількість транспортних засобів;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Підвищена безпека як для дорослих, так і для дітей, завдяки використаній периметральній житловій забудові, яка утворює закритий внутрішній двір;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Упорядкована прибудинкова територія, яка об&#39;єднує комфортні озеленені прогулянкові алеї, спортивний і дитячий майданчики;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Розвинена прибудинкова інфраструктура, представлена ​​дитячими й спортивними кімнатами, коворкінг-платформами;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Дизайнерський хол зі стійкою для ресепшіоніста й окремо відведеним місцем для консьєржа.</p>\r\n	</li>\r\n</ul>\r\n<h2 id=\"\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Характеристики житлового комплексу</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Комфортний житловий комплекс від будівельної компанії &quot;Інтергал-Буд&quot; проєктують за сучасною монолітно-каркасною технологією будівництва з використанням енергоефективних керамоблоків і мінеральної вати.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Індивідуальне опалення, передбачене в кожній квартирі в житловому комплексі &quot;Міллтаун&quot;, дає можливість самостійно контролювати кількість спожитого тепла, що позначається на подальших сімейних витратах на оплату комунальних послуг у зимову пору року.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Цікаво, що первинний забудовник &quot;Інтергал-Буд&quot; передбачив можливість купити один з 4-х варіантів нерухомості:</p>\r\n<ul style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	<li>\r\n		Квартиру (від 1-ї до 3-х кімнат) на 4-12 поверхах;</li>\r\n	<li>\r\n		Апартаменти на останніх поверхах;</li>\r\n	<li>\r\n		Офісні приміщення класу &quot;А&quot; з вільним і ергономічним плануванням;</li>\r\n	<li>\r\n		Комерційні об&#39;єкти для реалізації будь-якої бізнес-ідеї.</li>\r\n</ul>\r\n<h2 id=\"-1\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Особливості розвитку інфраструктури</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс &quot;Mill Town&quot; знаходиться за 10-15 хвилин ходьби від активної центральної частини Львова. Безпосередньо біля самого будинку функціонує торгово-розважальний центр &quot;Forum Lviv&quot;, у якому працює супермаркет &quot;Сільпо&quot;, сучасні магазини з одягом, кінотеатр і фудкорт. За 10 хвилин можна дійти до супермаркету &quot;Ашан&quot; і торгового центру &quot;New Point&quot;.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Діти дошкільного віку мають можливість відвідувати один з найближчих дитячих садків (№96, №104 і №144), а старші діти &mdash; школи №22, №53 та №87. Поблизу будинку також функціонує приватна християнська школа &quot;Перлина&quot;.</p>\r\n<h2 id=\"-2\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Цінова політика</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Оскільки ЖК &quot;Mill Town&quot; надає житло бізнес-класу у Львові в пішій доступності до популярного торгово-розважального центру &quot;Forum Lviv&quot;, то приблизна зафіксована вартість первинного квадратного метра в 34.2 тис. грн здається вельми виправданою.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Відповідно, покупка власної квартири в цьому житловому комплексі обійдеться приблизно в:</p>\r\n<ul style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	<li>\r\n		1-кімнатна квартира (від 39.3 кв. м) &mdash; близько 1.69 млн грн;</li>\r\n	<li>\r\n		2-кімнатна квартира (від 54.5 кв. м) &mdash; близько 2.12 млн грн;</li>\r\n	<li>\r\n		3-кімнатна квартира (від 84.7 кв. м) &mdash; близько 3.01 млн грн.</li>\r\n</ul>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	&nbsp;</p>\r\n', 1, 1, 1, 1, 0, 0, 0),
(17, 'ЖК Квартал Гімназійний 2', 1, 2, 4, 2, 140, 80, 30, 4, 10, 2, 8, 5000, 2000, '54325', 21400, 'Украина', 'Ивано-франковская', 'Івано-Франківськ', 'Пасічна 14Г', 0, 0, '<div class=\"AboutComponent__AboutWraper-sc-1i93neo-0 iIqvxe\" style=\"line-height: 25px; font-size: 18px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	<span style=\"font-size: 24px; line-height: 30px; font-weight: bold; margin: 25px 0px 0px;\">Про житловий комплекс</span>\r\n	<div style=\"margin: 25px 0px 0px;\">\r\n		Житловий комплекс Квартал Гімназійний 2 споруджується забудовником Левада. ЖК належить до новобудов комфорт класу. Здача об&#39;єкту в експлуатацію запланована на 4 квартал 2022 року. В процесі будівництва знаходиться 1 будинок.</div>\r\n</div>\r\n<div class=\"AboutComponent__AboutWraper-sc-1i93neo-0 iIqvxe\" style=\"line-height: 25px; font-size: 18px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	<span style=\"font-size: 24px; line-height: 30px; font-weight: bold; margin: 25px 0px 0px;\">Розташування</span>\r\n	<div style=\"margin: 25px 0px 0px;\">\r\n		ЖК Квартал Гімназійний 2 розташований в Івано-Франківську. Новобудову можна знайти безпосередньо в районі Пасічна, 14Г. Також варто звернути увагу на карту вище. Це допоможе зорієнтуватися без проблем, де саме будується ЖК і як до нього доїхати, якщо ви вирішили особисто перевірити хід будівництва. Карта також дозволить оцінити зручність локації для майбутнього проживання.</div>\r\n</div>\r\n<div class=\"AboutComponent__AboutWraper-sc-1i93neo-0 iIqvxe\" style=\"line-height: 25px; font-size: 18px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	<span style=\"font-size: 24px; line-height: 30px; font-weight: bold; margin: 25px 0px 0px;\">Технічні дані</span>\r\n	<div style=\"margin: 25px 0px 0px;\">\r\n		Новобуд зводять за технологією &ndash; монолітно - каркасна. Кількість поверхів у будинку &ndash; 9, висота стель &ndash; 2.7 м. Опалення &ndash; індивідуальне. Докладно розглянути доступні планування житла в цьому будинку, оцінити їх зручність та обрати конкретний варіант можна на нашому сайті або у відділі продажу.<br />\r\n		Якщо вас цікавить, де після покупки житла вам доведеться паркувати автомобіль, варто звернути увагу на наявність паркінгу чи стоянки біля будинку. В цьому житловому комплексі є паркування.</div>\r\n</div>\r\n<div class=\"AboutComponent__AboutWraper-sc-1i93neo-0 iIqvxe\" style=\"line-height: 25px; font-size: 18px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	<span style=\"font-size: 24px; line-height: 30px; font-weight: bold; margin: 25px 0px 0px;\">Особливості продажу квартир</span>\r\n	<div style=\"margin: 25px 0px 0px;\">\r\n		Житловий комплекс Квартал Гімназійний 2 на DOM.RIA представлений 4 плануваннями. В новобудові можна купити нерухомість за такими цінами від забудовника:\r\n		<ul>\r\n			<li>\r\n				1-кімнатні &ndash; площа 42.87 квадратних метрів, вартість 802&nbsp;809 грн;</li>\r\n			<li>\r\n				2-кімнатні &ndash; площа від 53.22 і до 67.95 квадратних метрів, вартість від 996&nbsp;629 і до 1&nbsp;272&nbsp;472 грн;</li>\r\n		</ul>\r\n		Про можливість придбання житла в кредит або в розстрочку, можна дізнатися на цій же сторінці або зв&#39;язавшись з представником забудовника.</div>\r\n</div>\r\n<div class=\"AboutComponent__AboutWraper-sc-1i93neo-0 iIqvxe\" style=\"line-height: 25px; font-size: 18px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	<span style=\"font-size: 24px; line-height: 30px; font-weight: bold; margin: 25px 0px 0px;\">Інфраструктура</span>\r\n	<div style=\"margin: 25px 0px 0px;\">\r\n		Житловий комплекс Квартал Гімназійний 2 відрізняється зручним розташуванням, оскільки в безпосередній близькості від нього є: навчальні заклади, школа, дитячий садок, лікарня, тц, ринок, магазини, паркові зони, транспортна розв&#39;язка, гаражний бокс. Інфраструктуру навколо новобудови та відстань від будинку до необхідних об&#39;єктів можна оцінити на карті.</div>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n', 1, 1, 0, 1, 1, 0, 0);
INSERT INTO `estate` (`id`, `name`, `type`, `market`, `rooms`, `walls`, `totalSq`, `residentialSq`, `kitchenSq`, `floor`, `superficiality`, `heating`, `yearOfConstruction`, `utilitiesWinter`, `utilitiesSummer`, `registrationNumber`, `price`, `country`, `oblast`, `city`, `adress`, `coord_x`, `coord_y`, `description`, `arenda_prodazha`, `remont`, `mebel`, `posle_stroiteley`, `parking`, `terasa`, `penthouse`) VALUES
(18, 'ЖК Скай Сіті (Sky City)', 1, 2, 4, 4, 160, 120, 30, 5, 15, 2, 9, 5000, 2000, '5353', 46500, 'Украина', 'Одесская', 'Одесса', '27а2', 0, 0, '<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житло комфорт-класу в Київському районі Одеси тепер можна купити в житловому комплексі Скай Сіті (Sky City)! Житловий комплекс являє собою сучасну 24-поверхову новобудову з лаконічними вентильованими фасадами й ергономічними плануваннями. Житловий комплекс &quot;Скай Сіті&quot; &mdash; це про квартиру в Одесі за 20 хвилин їзди на авто від центру в зеленому районі з розвиненою прибудинковою інфраструктурою!</p>\r\n<h2 id=\"\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Про житловий комплекс Скай Сіті в Одесі</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	ЖК Скай Сіті будує один з найпопулярніших українських забудовників &mdash; компанія &quot;Будова&quot;. Це означає продаж готових квартир за вигідною ціною, продумані й ергономічні планування, надійні будівельні технології та матеріали й здачу об&#39;єктів у термін. Житловий комплекс Скай Сіті в Одесі планують ввести в експлуатацію у 3 кварталі 2021 року. Серед важливих конкурентних переваг, які привертають увагу потенційних покупців, виділяють:</p>\r\n<ul style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	<li>\r\n		<p>\r\n			Вигідне географічне розташування в одному з найактивніших і розвинених районів Одеси;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Впорядковану, закриту й охоронювану територію прибудинкову з дитячими й спортивними майданчиками, зонами для відпочинку дорослих;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Використання передових будівельних технологій і матеріалів, включаючи рекуперацію повітряних потоків, індивідуальне зонування житлової та громадської площі й т. п.;</p>\r\n	</li>\r\n	<li>\r\n		<p>\r\n			Доступна вартість як компактних 1-кімнатних квартир, так і просторих 3-кімнатних апартаментів на верхніх поверхах.</p>\r\n	</li>\r\n</ul>\r\n<h2 id=\"-1\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Характеристики житлового комплексу</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Новий 24-х поверховий будинок з висотою стель у 3 метри зводиться за монолітно-каркасною технологією. Для цього використовують каркас з надійного залізобетону, а для стін і перегородок вибирають звичні будівельні матеріали. Така новобудова виходить міцною, сейсмостійкою і дає можливість забудовнику створювати цікаві й практичні планування квартир.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Підземний паркінг на території ЖК Скай Сіті розрахований на достатню кількість машин і дає можливість звільнити двір від машин. Автономне опалення не тільки зручне, але й економічно вигідне для майбутніх мешканців. Квартири здаються під чистову обробку. Всього в комплексі будується 5 секцій, усі вони на цей момент активно готуються до здачі. Вже підведені комунікації, виконані фасадні й покрівельні роботи. Під час внутрішніх робіт зводять перегородки, заливають стяжку підлоги й штукатурять стіни. Фінальним етапом стане благоустрій прибудинкової території від забудовника &quot;Будова&quot;.</p>\r\n<h2 id=\"-2\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Прибудинкова інфраструктура</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Закупитися необхідними товарами можна в супермаркеті &quot;Копійка&quot;. У пішій доступності також функціонують супермаркети &quot;Таврія-В&quot; і &quot;Сільпо&quot;. Діти шкільного віку мають можливість відвідувати школу №100 (5 хвилин), №33, №46 та №63 (10 хвилин). Для найменших представників цього житлового комплексу відкриті двері дитячого садка Чорноморський.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс Sky City знаходиться поруч з парком Горького. На великий і впорядкованій території функціонують популярні ресторани, кав&#39;ярні, атракціони й квести для дітей.</p>\r\n<h2 id=\"-3\" style=\"font-size: 24px; line-height: 30px; margin: 25px 0px 0px; color: rgb(0, 0, 0); font-family: Roboto2, sans-serif;\">\r\n	Цінова політика</h2>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Вартість квадратного метра в житловому комплексі &quot;Скай Сіті&quot; варіюється в межах 23.4 тис. грн. Отже, покупка первинної нерухомості, наприклад, 1-кімнатної квартири від 38.9 квадратних метрів обійдеться приблизно в 990 тис. грн.</p>\r\n', 0, 1, 0, 1, 1, 1, 0),
(19, 'ЖК Чайка', 1, 2, 3, 4, 200, 150, 30, 7, 15, 2, 5, 3000, 2000, '4554235', 28000, 'Украина', 'Киевская', 'Киев', 'вул. Валерія Лобановського, 1–31', 0, 0, '<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Інформація про житловий комплекс</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	ЖК &laquo;Чайка&raquo; будується в с. Чайки Києво-Святошинського району Київської області за адресою вул. Валерія Лобановського, 1-31. Новобудови розташовані на території санаторно-курортної зони та з усіх боків оточені зеленими насадженнями. Як видно на карті, найближча станція метро &ndash; &laquo;Житомирська&raquo; &ndash; знаходиться на відстані 7 км, в 10 хвилинах їзди.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий масив Чайка в передмісті Києва включає в себе 33 будинки комфорт-класу. Забудовником виступає девелоперська компанія &laquo;ОМОКС&raquo;. Повністю ввести об&#39;єкт в експлуатацію планують в II кварталі 2021 року.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Особливості та переваги</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс зводять по цегляній технології, стіни &ndash; з керамічної червоної цегли. Будинки мають змінну поверховість &ndash; від 6 до 15 рівнів. У забудовника можна купити квартиру в ЖК Чайка з чистовою обробкою та індивідуальним опаленням.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Плюси проживання в новому будинку по вулиці В. Лобановського:</p>\r\n<ul style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	<li>\r\n		хороша транспортна доступність столиці;</li>\r\n	<li>\r\n		свій парк площею 39 га;</li>\r\n	<li>\r\n		закритий і затишний двір з 7 в&#39;їзними групами та цілодобовою охороною;</li>\r\n	<li>\r\n		своя котельня та індивідуальне опалення;</li>\r\n	<li>\r\n		комерційні приміщення для бізнесу;</li>\r\n	<li>\r\n		більше 40 інфраструктурних об&#39;єктів, серед яких школи та дитсадки.</li>\r\n</ul>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Об&#39;єкти інфраструктури</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Житловий комплекс Чайка оснащений усім необхідним для комфортного проживання. На його території працюють 2 школи, 2 дитячих садки, центри розвитку, тенісна академія з тенісним кортом, футбольна академія з футбольним полем. Також відкриті фітнес-клуби, медичний центр, аптеки, пошта, банки, кав&#39;ярні, ресторани, салони краси, магазини.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	У дворі обладнані вуличні спортивні комплекси, баскетбольний майданчик, 20 ігрових зон для дітей. Для машин передбачено понад 2000 місць на відкритих автостоянках і критий багаторівневий паркінг. У великому парку є затишні зони відпочинку та алеї для прогулянок.</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Планування та покупка квартир</p>\r\n<p style=\"color: rgb(0, 0, 0); font-family: Roboto2, sans-serif; font-size: 18px;\">\r\n	Забудовник пропонує покупцям понад 80 типових планувань житла.&nbsp; У продажу &ndash; 1-2-3-4-кімнатні варіанти нерухомості під будь-які потреби. Дізнатися ціни на вподобані квартири, умови оформлення розстрочки або кредиту можна у відділі продажів, з яким можна зв&#39;язатися по вказаному телефону.</p>\r\n', 1, 1, 1, 1, 1, 1, 0),
(20, 'Офіс', 4, 2, 1, 1, 66, 65.3, 0, 3, 3, 1, 7, 500, 300, '1', 800, 'Україна', '', 'Київ', 'Воздухофлотский проспект 90', 0, 0, '<p style=\"text-align: justify;\">\r\n	Оренда нового офісного приміщення без комісії. В приміщенні є свій с/в, цілодобова охорона, відеоспостереження, парківн, доступ 24/7. Поблизу аеропорт Жуляни та ЖД станція Київ-Волинський.</p>\r\n', 0, 1, 0, 0, 1, 0, 0),
(21, 'гаражний бокс', 4, 1, 1, 10, 32, 0, 0, 0, 1, 3, 12, 150, 150, '2', 100, 'Україна', '', 'Київ', 'проспект Бандери 30', 0, 0, '<p>\r\n	Оренда гаражного боксу без комісії. Гаражно-будівельний кооператив &quot;Мостовик&quot;, загальна площа 32,8 кв.м., два поверхи: перший та підвал, зручне місцезнаходження, можливо використовувати як склад, гараж, є яма. Перший поверх: розміри 2.94х5.68м, Н=2.28. Підвал складається з двох приміщень 2.29х2.74м та 2.92х2.77м, Н=1.74.</p>\r\n', 0, 0, 0, 0, 0, 0, 0),
(22, 'Однокімнатна квартира', 1, 1, 1, 1, 35, 30, 6, 0, 5, 1, 1, 0, 0, '', 6000, 'Україна', '', 'Київ', 'просп. Червоної Каліні (Маяковського) 32Д', 0, 0, '<p>\r\n	Здам в оренду простору квартиру на Троєщині<br />\r\n	просп. Червоної Каліні (Маяковського) 32Д, 1 поверх, на вікнах решітки<br />\r\n	В наявності необхідні меблі та техніка<br />\r\n	Балкон затеклений<br />\r\n	Квартира дуже тепла<br />\r\n	Поруч Фестивальний<br />\r\n	Зручна транспортна розв&#39;язка</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 0, 1, 1, 0, 0, 0, 0),
(23, 'Однокімнатна квартира', 1, 1, 1, 1, 34, 30, 5, 0, 1, 1, 1, 0, 0, '', 5000, 'Україна', '', 'Київ', 'пр.Червоної калини (Маяковського) 79а', 0, 0, '<p>\r\n	Здам в оренду однокімнатну на Троещині, пр.Червоної калини (Маяковського) 79а, квартира дуже тепла. Будинок в дворі, тому не чути шума з дороги. До зупинки, магазинів та базарчика 3 хвилини . До парка 7 хвилин, Спорт лайф через дорогу. Квартира дуже чиста та охайна.</p>\r\n', 0, 1, 1, 0, 0, 0, 0),
(24, 'Трикімнатна квартира', 1, 2, 3, 1, 86, 80, 10, 7, 16, 1, 1, 0, 0, '', 1300, 'Україна', '', 'Київ', 'вул.Коновальця 36В', 0, 0, '<p>\r\n	Здам простору квартиру в центрі Києва вул.Коновальця 36В. Новий ремонт, перша здача. Є власне паркомісце в підземному паркінгу. Три окремі кімнати чудово підійде для родини з дітьми. Поруч безліч кафе, ресторанів, продуктові магазини, аптеки, бізнес центри, спорт зал. Метро Печерська 5 хв пішки.Ціна 1300у.е.</p>\r\n', 0, 1, 1, 1, 1, 0, 0),
(25, 'Однокімнатна квартира', 1, 2, 1, 1, 36, 32, 8, 19, 25, 1, 8, 0, 0, '', 0, 'Україна', '', 'Київ', 'Метро Вирлиця', 0, 0, '<p>\r\n	Здам квартиру площею 36м2 на 19-му поверсі з видом на озеро. Новобудова, свіжий ремонт, укомплектована новими меблями, новою технікою, бойлером, кондиціонером і т.д. Метро Вирлиця, 3 хвилини. Гарний 25 поверховий будинок зі своєю обнесеною огорожею територією. Парковка.Квартира в білих тонах.</p>\r\n', 0, 1, 1, 1, 0, 0, 0),
(26, 'Однокімнатна квартира', 1, 2, 1, 1, 43, 40, 9, 13, 25, 1, 1, 0, 0, '', 9000, 'Україна', '', 'Київ', 'вул.Срібнокільська 1', 0, 0, '<p>\r\n	ЖК Бізнес класу Корона вул.Срібнокільська 1 Квартира з повним комплектом сучасних меблів та техніки. Бойлер, посудомийка,смарт ТБ У кожній кімнаті кондиціонер Два санвузли (ванна та душ кабіна) Два засклені балкони Дві величезні шафи купе Чотири ліфти, консьєрж, відеоспостереження На території ЖК є все необхідне До станції метро Позняки 7-10 хвилин прогулянковим кроком В межах 1 км безліч розважальних закладів, ТРЦ Аладін і Піраміда, магазини, ресторани, кав&#39;ярні, парк, озеро.</p>\r\n', 0, 1, 1, 1, 0, 0, 0),
(27, 'Двокімнатна квартира', 1, 2, 1, 1, 63, 55, 10, 6, 25, 1, 9, 0, 0, '', 12000, 'Україна', '', 'Київ', 'вул.Антоновича 158', 0, 0, '<p>\r\n	Оренда 2к квартири в центрі міста вул.Антоновича 158, світло відключають рідко! Метро Либідська 5-7 хв пішки Власник може докупити ще одне ліжко під запит орендаря. В квартирі є кондиціонер. Поруч з домом є супермаркети, ТРЦ Оушен Плаза, кафе, ресторати.</p>\r\n', 0, 1, 1, 1, 0, 0, 0),
(28, 'Однокімнатна квартира', 1, 2, 1, 1, 40, 35, 8, 0, 1, 1, 8, 0, 0, '', 10000, 'Україна', '', 'Київ', 'Січових Стрільців 44а', 0, 0, '<p>\r\n	Орнеда просторої квартири в сучасному ЖК А52 на Січових Стрільців 44а. Повністю закрика та охороняєма территорія. Є підземний паркінг який можна використовувати як укриття. Цілодобове відео спостереження + консьерж сервіс Квартира з новим ремонтом Є бойлер, кондиціонер, посудомийка, інтернет На даху будинку простора лаунж зона для всіх мешканців будинку. Поруч є сквер та вихід до храму, продуктові магазини, кафе, ресторани.</p>\r\n', 0, 1, 1, 1, 1, 0, 0),
(29, 'Продаж 1к квартири 50 кв. м на вул. Гарматна 37А', 1, 1, 1, 1, 50, 20, 13, 12, 24, 1, 12, 1200, 500, '43553', 89000, 'Украина', 'Киевская', 'Киев', 'вул. Гарматна 37А', 50.4447, 30.4323, '<ul class=\"unstyle\" style=\"box-sizing: border-box; scroll-behavior: smooth; margin: 0px; padding: 0px; list-style: none; color: rgb(0, 0, 0); font-family: Roboto; font-size: 16px;\">\r\n	<li class=\"mb-10\" style=\"box-sizing: border-box; scroll-behavior: smooth; margin-bottom: 10px;\">\r\n		<div class=\"boxed descriptionBlock\" id=\"descriptionBlock\" style=\"box-sizing: border-box; scroll-behavior: smooth; width: 925px; display: inline-block; word-break: break-word;\">\r\n			Продам власну однокімнатну квартиру, в ЖК &laquo;Гарматна Західна&raquo;. Квартира продається з меблями, та побутовою технікою, в відмінному стані.<br style=\"box-sizing: border-box; scroll-behavior: smooth;\" />\r\n			В будинку власна котельня, опалення централізоване, оплата згідно поквартирного лічильника. Охайне парадне і ліфт. В будинках є підземний паркінг. Будинок обслуговує ОСББ, робить це якісно, забезпечуючи комфорт і зручність проживаючих. Зручна транспортна розвязка, пішки 5 хв швидкісний трамвай, 30 хв метро Шулявка. Аптеки, магазини, школа садочок, університет НАУ, все в пішій доступності. Через дорогу парк &laquo;Орлятко&raquo;, де є дитячі ігрові майданчики, озеро, готель, ресторан.. ЖК &laquo;Гарматна-Західна&raquo;, обєднує два будинки - один будинок по вул. Гарматна 37, другий будинок по вул Західна 10. Заїзд через вул Гарматна, виїзд через вулицю Західна. Квартира в будинку за адресою Західна 10.<br style=\"box-sizing: border-box; scroll-behavior: smooth;\" />\r\n			Я власник, є питання дзвоніть. Але зручніше писати на Viber, WhatsApp, Telegram в любий час. Без комісії, без посередників!</div>\r\n	</li>\r\n</ul>\r\n<ul class=\"unstyle mt-20\" data-v-39d4dc34=\"\" id=\"additionalInfo\" style=\"box-sizing: border-box; scroll-behavior: smooth; margin: 0px; padding: 0px; list-style: none; color: rgb(0, 0, 0); font-family: Roboto; font-size: 16px;\">\r\n	<li class=\"mt-20 flex\" data-v-39d4dc34=\"\" style=\"box-sizing: border-box; scroll-behavior: smooth; margin-top: 0px; display: flex;\">\r\n		<div class=\"boxed\" data-v-39d4dc34=\"\" style=\"box-sizing: border-box; scroll-behavior: smooth; width: 925px; display: inline-block;\">\r\n			<div data-v-39d4dc34=\"\" style=\"box-sizing: border-box; scroll-behavior: smooth;\">\r\n				Характеристика приміщення: утеплення: без утеплення</div>\r\n		</div>\r\n	</li>\r\n	<li class=\"mt-20 flex\" data-v-39d4dc34=\"\" style=\"box-sizing: border-box; scroll-behavior: smooth; margin-top: 20px; display: flex;\">\r\n		<div class=\"boxed\" data-v-39d4dc34=\"\" style=\"box-sizing: border-box; scroll-behavior: smooth; width: 925px; display: inline-block;\">\r\n			<div data-v-39d4dc34=\"\" style=\"box-sizing: border-box; scroll-behavior: smooth;\">\r\n				Двері та вікна: тип вікон: без вікон</div>\r\n		</div>\r\n	</li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n', 1, 1, 1, 0, 0, 0, 0),
(30, 'Продаж 2к квартири 57 кв. м на вул. Маршала Конєва 5Б', 1, 2, 2, 1, 57, 31, 9, 10, 24, 1, 1, 1500, 800, '545435', 95000, 'Украина', 'Киевская', 'Киев', 'вул. Маршала Конєва 5Б', 50.3821, 30.4646, '<ul class=\"unstyle\" style=\"box-sizing: border-box; scroll-behavior: smooth; margin: 0px; padding: 0px; list-style: none; color: rgb(0, 0, 0); font-family: Roboto; font-size: 16px;\">\r\n	<li class=\"mb-10\" style=\"box-sizing: border-box; scroll-behavior: smooth; margin-bottom: 10px;\">\r\n		<div class=\"boxed descriptionBlock\" id=\"descriptionBlock\" style=\"box-sizing: border-box; scroll-behavior: smooth; width: 925px; display: inline-block; word-break: break-word;\">\r\n			Самійла Кішки ( Маршала Конєва), буд. 5 Б, ЖК &laquo; Еврика&raquo;.<br style=\"box-sizing: border-box; scroll-behavior: smooth;\" />\r\n			Поверх 10.<br style=\"box-sizing: border-box; scroll-behavior: smooth;\" />\r\n			Загальна площа 56, 62 м.кв.<br style=\"box-sizing: border-box; scroll-behavior: smooth;\" />\r\n			Квартира видова, світла та простора, раціональне планування, тепло лічильник на квартиру , зроблений якісний ремонт , повністю укомплектована необхідною технікою та меблями.<br style=\"box-sizing: border-box; scroll-behavior: smooth;\" />\r\n			Зразкове парадне та двір, поряд озера, парк ВДНГ та Голосіївський парк, садок, школа, супермаркети, різноманітні магазини, ресторани та кафе, спорткомплекс Спортлайф з басейном, наявність парковки.<br style=\"box-sizing: border-box; scroll-behavior: smooth;\" />\r\n			Пішки до станції метро Іподром 5 хвилин, до станції метро Виставковий центр (ВДНГ) 15 хвилин.<br style=\"box-sizing: border-box; scroll-behavior: smooth;\" />\r\n			Ціна: 95 000 у.о. ( з урахуванням переуступки!)</div>\r\n	</li>\r\n</ul>\r\n', 1, 1, 1, 1, 0, 0, 0);
